import React from 'react';
import Slider from "react-slick";
import { makeStyles } from '@material-ui/core/styles';
import portfolio from "../Images/portfolio.JPG";
import { useProjectsContext } from "../Utils/ProjectsContext"


const useStyles = makeStyles((theme) => ({
    root: {
        margin: 'auto',
        color: "black",
        marginBottom: "50px",
        textAlign:"justify",
        width: "100%",
    }

}));



export default function Slides() {

    const [state, dispatch] = useProjectsContext();
    const classes = useStyles();
    return (
        <div className="CardSlides">
            <h1>Recent Projects</h1>
            { state.map((project, i) => (

                    project.fields.images.map(projectImages => (

                    < Slider
                    dots={true}
                    fade= {true}
                    infinite={true}
                    speed={500}
                    slidesToShow={1}
                    slidesToScroll= {1} >


                        {/* cardName: project.fields.projectName,
                        cardDescription: project.fields.description,
                        cardTotal: project.sys.id,
                        showBodyImage: true, */}

                            <img src={projectImages.fields.file.url}/>
                        </ Slider>



                ))
            ))}
</div>


    )
}
