import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Carousel from 'react-material-ui-carousel';
import { Paper, IconButton,CardActions } from '@material-ui/core';
import { GitHub, LiveTv } from '@material-ui/icons';
import { useProjectsContext } from "../Utils/ProjectsContext";

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "80%",
        margin: 'auto',
        color: "black",
        marginBottom: "50px",
        textAlign: "justify"
    },
    media: {
        // alignItems: "center",
        marginLeft: "100px",
        // marginRight:"100px",
        // width: "40%",
        // height: "auto",
        // objectFit: "cover"
        margin: "auto",
        width: "50%",
        border: "3px solid green",
        padding: "10px"
    }
}));

export default function Example(props) {
    const [state, dispatch] = useProjectsContext();
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <h1 className='technology'>Recent Projects</h1>
            <Carousel>
                {
                    // items.map( (item, i) => <Item key={i} item={item} /> )
                    state.map((project, i) => (
                        project.fields.images.map(projectImages => (
                            <Paper>
                                <h1>{project.fields.projectName}</h1>

                                <IconButton
                                    aria-label="live url"
                                    onClick={() => window.open((project.fields.liveLink), '_blank')}
                                >
                                    <LiveTv />
                                </IconButton>

                                <IconButton
                                    aria-label="Github"
                                    onClick={() => window.open((project.fields.githubLink), '_blank')}
                                >
                                    <GitHub />
                                </IconButton>

                                <p>{project.fields.description}</p>
                                <img src={projectImages.fields.file.url} className={classes.media}></img>



                            </Paper>

                        ))))
                }
            </Carousel>
        </div>
    )
}
