import React from 'react';
import { CardSlide } from 'react-card-slide/dist';
import { makeStyles } from '@material-ui/core/styles';
import portfolio from "../Images/portfolio.JPG";
import { useProjectsContext } from "../Utils/ProjectsContext"


const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "80%",
        margin: 'auto',
        color: "black",
        marginBottom: "50px",
        textAlign:"justify"
    },
    media: {
        display: "block",
        margin: "auto",
        width: "25%",
        paddingTop: '20.25%', // 16:9

    },
    expand: {
        transform: 'rotate(0deg)',
        margin: 'auto',

        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',

    },

    cardHeader: {
        backgroundColor: "#ffffff"
    },
    cardFooter: {
        backgroundColor: "#ffffff"
    },
    description: {
        color: "#112222"
    }
}));


export default function Slides() {

    const [state, dispatch] = useProjectsContext();
    const classes = useStyles();
    return (
        <div className="CardSlides">
            <h1>Recent Projects</h1>
            { state.map((project, i) => (

                    project.fields.images.map(projectImages => (



                    < CardSlide
                    className={classes.root}
                        items = {
                    [{
                        cardName: project.fields.projectName,
                        cardDescription: project.fields.description,
                        cardTotal: project.sys.id,
                        showBodyImage: true,
                        bodyImage: projectImages.fields.file.url,
                    }]} />


                ))
            ))}

        </div>


    )
}
