import React, { useState } from 'react';
import { Paper, Grid, makeStyles, Typography, Box, Container } from '@material-ui/core';

//Import profile image
import portfolio from "../Images/pradeepPhoto.jpg"

//Using custom css
const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        margin: '30px',
        maxWidth: "auto",
        backgroundColor: "#f0ead2",
        color: "#033500",
        borderRadius: "40px"
    },

    img: {
        width: 130,
        height: 200,
        display: 'block',
        maxWidth: '100%',
        maxHeight: '100%',
        borderRadius: "100px",
        display: "block",
        marginLeft: "auto",
        marginRight: "auto",
    },
    aboutMeTitle: {
        textAlign: "center",
        color: "#373e02",

    },
    aboutMe: {
        textAlign: "left"

    }
}));

export default function UsedTechnologies() {

    const classes = useStyles();

    //Setting state
    const [title, setTitle] = useState("Used Technologies")
    const [frontEndtechnologies, setTechnologies] = useState(
        [
            "HTML",
            "CSS", "Javascript", "Jquery",
            "React and React Hooks (useState,useReducer,useRef,useHistory,useContext)", "Bootstrap", "Material UI", "API",
            "NODEJS",
            "EXPRESS",
            "MYSQL",
            "SEQUELIZE",
            "ORM",
            "MONGODB",
            "MONGOOSE",
            "bcryptjs", "jsonwebtoken", "multer", "passport-local", "socket.io"
        ])

    return (

        <div className={classes.root}>
            <Paper className={classes.paper}>
                <Typography variant="h2" className={classes.aboutMeTitle} gutterBottom >{title}</Typography>
                <hr />
                <Grid item xs container spacing={3}>
                    <Grid item xs={12} md={6} className={classes.aboutMe}>
                        <Typography variant="h4">Front End Technologies</Typography>
                        <hr />
                        {technologies.map(technology => (
                                    <Typography variant="h6">{technology}</Typography>
                                ))}


                    </Grid>

                    <Grid item xs={12} md={6}> 
                     <Typography variant="h4" >Back End Technologies</Typography>
                        <hr />
                                {technologies.map(technology => (
                                    <Typography variant="h6">{technology}</Typography>
                                ))}
                    </Grid>
                </Grid>

            </Paper>
        </div>
    );
}
