import React from 'react';
import { Typography, Link, makeStyles, Paper, Grid } from '@material-ui/core';
import { Assignment, Email, Phone, Home } from '@material-ui/icons';
//import FileSaver from "file-saver"
//import axios from "axios"
import { saveAs, encodeBase64 } from "@progress/kendo-file-saver"


//Using custom styles
const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        margin: '30px',
        maxWidth: "100%",
        backgroundColor: "#f8deb8",
        textAlign: "center",
        borderRadius: "40px"
    }
}));


const saveFile = (event) => {
    // fileSaver.saveAs(
    //     process.env.PUBLIC_URL + "/new_Resume_Pradeep_Marasini.pdf",
    //     "MyCV.pdf"
    // );

    // var blob = new Blob([process.env.PUBLIC_URL + "new_Resume_Pradeep_Marasini.pdf"], { type: "text/plain;charset=utf-8" });
    // FileSaver.saveAs(blob, "MyCV.pdf");
    // FileSaver.saveAs("https://drive.google.com/file/d/1eFrO_qEzWZDXEq5cyVtMUSyOKI-PjZhF/view?usp=sharing", "MyCV.pdf");

      event.preventDefault();
      const url = "https://drive.google.com/file/d/1eFrO_qEzWZDXEq5cyVtMUSyOKI-PjZhF/view?usp=sharing"
      var xhr = new XMLHttpRequest()
      xhr.open(url,'GET',true)
      xhr.responseType='blob'

      xhr.onload = function(){
          var file = new Blob([xhr.response])
      }
};
export default function ContactMe() {

    //Importing Hooks
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Paper className={classes.paper}>
                <Grid item xs container spacing={3}>
                    <Grid item xs={12} md={12}>
                        <Link component="button" variant="h5" >
                            {/* <a href="https://drive.google.com/file/d/1eFrO_qEzWZDXEq5cyVtMUSyOKI-PjZhF/view?usp=sharing" target="_blank">DownLoad CV </a><Assignment /></Link> */}
                            <button className="cv" onClick={saveFile}>
                                Download File
</button>
                        </Link>

                    </Grid>

                    <Grid item xs={12} md={12}>
                        <Typography component="address" variant="h5" > Email <Email />
                            <Link component="button" variant="h5">
                                marasinipradeep@gmail.com</Link>
                        </Typography>
                    </Grid>

                    <Grid item xs={12} md={12}>
                        <Typography variant="h5" >
                            Phone<Phone />: <Link variant="h5" > 0420719901</Link>
                        </Typography>

                    </Grid>
                    <Grid item xs={12} md={12}>
                        <Typography component="address" variant="h5" >
                            Address<Home /> : 4/10 Lurline Av, Gilles Plain South Australia
                            </Typography>
                    </Grid>
                </Grid>
            </Paper>
        </div>
    );
}
