import React, { useState } from 'react';
import Project from './techProject';
import Fade from 'react-reveal/Fade';

import { FaLaptop, FaLaptopCode, FaDatabase, FaExpand } from "react-icons/fa"


//import from services css
//import './Technologies.css'

function Services() {

    const [tech,setTech] = useState(
        {

            //Contact Email
            contactEmail:'marasinipradeep@gmail.com',
            // Add Your About Text Here
            abouttext: "I am front end Web Developer experience in building, and maintaining responsive website. Proficient in HTML5, CSS3, JavaScript, jQuery, Bootstrap, Material UI, Foundation, plus designing knowledge of icons, logo and well versed in Scrum, Agile. Certified in full stack web development from University of Adelaide, trained with  MERN stack (Mongo DB, Express Server, React JS, Node JS), PWA (Progressive Web App), testing with Jest, and familiar with JIRA, Github and Gitlab",
            aboutImage:portfolio,
           //Change This To Hide The Image of About Section (True Or False)
           ShowAboutImage:true,//true or false (Change Here)
           // Change Projects Here 
           projects:[
               {
                id: 1,//DO NOT CHANGE THIS (Please)ðŸ˜…
                title:'Go-Employee', //Project Title - Add Your Project Title Here
                 service:'This application helps to establish a connection between industry owner and job seeker. This is useful for business owner to find workers instantly in busy time. And benefits worker  because they can register, add information, and put themselves online so hiring person can see, view them and get connect.', // Add Your Service Type Here
                //Project Image - Add Your Project Image Here
                 imageSrc:goemployee,
                 //Project URL - Add Your Project Url Here
                 url:'https://goemployees.herokuapp.com/'
                },
                {
                    id: 2,//DO NOT CHANGE THIS (Please)ðŸ˜…
                    title: 'React-Portfolio',
                    service: 'This is a portfolio, which is useful to presents personal information, evidence of relevant skills and abilities.The website provides personal information in depth which includes personal introduction, list of proficient technologies, contact information and list of projects that have been accomplished.',
                    imageSrc: reactPortfolio,
                    url: 'https://pradeep-marasini.herokuapp.com/'
                },
                { 
                    id: 3,//DO NOT CHANGE THIS (Please)ðŸ˜…
                    title: 'Bin-Out',
                    service: 'Bin-out is an application which allows users to submit a request for their bins to be taken out for collection when they are unable to. Once a request is sent, a local employee can choose to accept and fulfil the request. This application is useful for people who are unable to put their bins out, potentially due to being away from their property, having a physical condition or being elderly.',
                    imageSrc: binOut,
                    url: 'binout.herokuapp.com/'
                },
                {
                    id: 4,//DO NOT CHANGE THIS (Please)ðŸ˜…
                    title: 'Music-App',
                    service: 'This is a music application that plays a song based upon responses to questions.This application helps to find a perfect song and play when a person is unable to decide what song they should listen. So user are provided with series of random questions with options to choose and at the end app plays a song.',
                    imageSrc: musicApp,
                    url: 'https://marasinipradeep.github.io/music-app/'
               },

               {
                id: 5,
                title: 'Weather Dashboard',
                service: 'This is a weather applications.This application will help traveler to see the weather outlook for multiple cities, so that they can plan a trip accordingly',
                imageSrc:weatherDashboard,
                url: 'https://marasinipradeep.github.io/weather_dashboard/'
            },

            {
                id: 6,
                title: 'Employee Directory',
                service:'By use of this application employee or manager would benefit greatly from being able to view non-sensitive data about other employees. It would be particularly helpful to be able to filter employees by name.',
                imageSrc:employeeDirectory,
                url: 'https://marasinipradeep.github.io/employee-directory/'
            },
               {
                    id: 7,
                    title: 'BLB(Branchless Banking)',
                    service: 'This application make easy for non-banking customers to be a part of the financial fabric using technology. BLB enables a number of banking partners to offer their services to remote districts of Nepal where modern bank branches are not established. This innovative solution provide features to deposit, withdraw, fund transfer and pay bills without establishment of physical branch saving huge amount.',
                    imageSrc:blb,
                    url: 'https://citytech.global/'
                }

            ],
            social: [
                // Add Or Remove The Link Accordingly
                {   name:'Github',
                    url:'https://github.com/marasinipradeep/'},
                {
                    name: 'LinkedIn',
                    url: 'https://www.linkedin.com/in/pmarasini'
                },
                {
                    name: 'Portfolio',
                    url: 'https://pradeep-marasini.herokuapp.com/'
                }
            ]
        }
    )

    const [frontEndTechnologies, setfrontEndTechnologies] = useState(
        [
            "HTML",
            "CSS",
            "Javascript",
            "jQuery",
            "C/C++",
            "Handlebars JS",
            "JSON",
            "JSX",
            "React JS",
            "Bootstrap",
            "Material UI",
            "Qt GUI",

        ])

    const [backEndTechnologies, setbackEndTechnologies] = useState(
        [
            "Node JS",
            "Express JS",
            "Libraries: Bcryptjs, JSON webtoken, Multer, Passport-local, socket.io"
        ])

    const [databaseTechnologies, setdatabaseTechnologies] = useState(
        [
            "MySQL",
            "MongoDB",
            "Sqllite",
            "PostgreSQL",
            "ORM: Sequelize",
            "ORM: Mongoose",
        ])

    const [testingTechnologies, settestingTechnologies] = useState(
        [
            "Jest",
            "Bitbucket",
            "Jira",
            "Github",
            "Gitlab",
            "Heroku"
        ])


    const [state, setState] = useState({
        technologies: [
            {
                icon: <FaLaptop />,
                title: "Front End",
                technology: frontEndTechnologies.map(frontend => {
                    return <ul>
                        <li>
                            {frontend}
                        </li>
                    </ul>
                })

            },
            {
                icon: <FaLaptopCode />,
                title: "Back End",
                technology: backEndTechnologies.map(backend => {
                    return <ul>
                        <li>
                            {backend}
                        </li>
                    </ul>
                })

            },
            {
                icon: <FaDatabase />,
                title: "Database",
                technology: databaseTechnologies.map(database => {
                    return <ul>
                        <li >
                            {database}
                        </li>
                    </ul>
                })

            },
            {
                icon: <FaExpand />,
                title: "Testing/Others",
                technology: testingTechnologies.map(testing => {
                    return <ul>
                        <li>
                            {testing}
                        </li>
                    </ul>
                })

            },
        ]

    })
    return (
        <div>
            <h1 className='heading'>
                <Fade bottom cascade>Work.</Fade></h1>
            <div className='work-content'>
                {state.technologies.map((project) =>(

                    <Project key={project.title}
                        title={project.icon}
                    >

                    </Project>
                ))}
            </div>
        </div>



    );
}


export default Services
