import React, { useState } from 'react'

import { FaPeopleCarry, FaLaptopCode, FaShuttleVan, FaAngular } from "react-icons/fa"


//import from services css
import './Technologies.css'

function Services() {

    const [frontEndTechnologies, setfrontEndTechnologies] = useState(
        [
            "HTML",
            "CSS",
            "JAVASCRIPT",
            "JQUERY",
            "C/C++",
            "HANDLEBAR",
            "JSON",
            "JSX",
            "REACT",
            "BOOTSTRAP",
            "MATERIAL UI",
            "QT-GUI",

        ])

        const [backEndTechnologies, setbackEndTechnologies] = useState(
            [
                "NODEJS",
                "EXPRESS",
                "Libraries: bcryptjs, jsonwebtoken, multer, passport-local, socket.io"
            ])

            const [databaseTechnologies, setdatabaseTechnologies] = useState(
                [
                    "MYSQL",
                    "MONGODB",
                    "SQLLITE",
                    "POSTGRESQL",
                    "ORM: SEQUELIZE",
                    "ORM: MONGOOSE",
                ])


    const [state, setState] = useState({
        technologies: [
            {
                icon: <FaPeopleCarry />,
                title: "Front End",
                info: frontEndTechnologies.map(frontend => {
                    return <ul>
                        <li>
                            {frontend}
                        </li>
                    </ul>
                })

            },
            {
                icon: <FaLaptopCode />,
                title: "Back End",
                info: backEndTechnologies.map(backend => {
                    return <ul>
                        <li>
                            {backend}
                        </li>
                    </ul>
                })

            },
            {
                icon: <FaShuttleVan />,
                title: "IT consultant",
                info: databaseTechnologies.map(database => {
                    return <ul>
                        <li>
                            {database}
                        </li>
                    </ul>
                })

            },
            {
                icon: <FaAngular />,
                title: "web development course",
                info: 'Coming Soon...................'

            },
        ]

    })
    return (
        <section className="services">
            <h1>Technical Proficiencies</h1>
            <div className="services-center">
                {state.technologies.map((item, index) => {
                    return (
                        <article key={index} className="services">
                            <span>{item.icon}</span>
                            <h2>{item.title}</h2>
                            <p>{item.info}</p>
                        </article>
                    );

                })}
            </div>
        </section>



    );
}


export default Services
