import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import { Card, CardHeader, CardMedia, CardContent, CardActions, Collapse, Avatar, IconButton, Typography } from '@material-ui/core';
import { FaLaptop, FaLaptopCode, FaDatabase, FaExpand } from "react-icons/fa"
import { red, blue } from '@material-ui/core/colors';
import { GitHub, LiveTv } from '@material-ui/icons';

import { useProjectsContext } from "../Utils/ProjectsContext"
import { Grid } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "80%",
        margin: 'auto',
        backgroundColor: "#e2c779",
        color: "black",
        marginBottom: "50px"
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9

    },
    expand: {
        transform: 'rotate(0deg)',
        margin: 'auto',

        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',

    },
    avatar: {
        backgroundColor: red[500],
        fontSize: "35px"
    },
    cardHeader: {
        backgroundColor: "#faf0db"
    },
    cardFooter: {
        backgroundColor: "#f3e5ab"
    },
    description: {
        color: "#112222"
    }
}));



export default function Services() {

    //importing useProject context 
        const [state, setState] = useState(
        {

            projects: [
                {
                    id: 1,//DO NOT CHANGE THIS (Please)ðŸ˜…
                    title: 'Go-Employee', //Project Title - Add Your Project Title Here
                    service: 'This application helps to establish a connection between industry owner and job seeker. This is useful for business owner to find workers instantly in busy time. And benefits worker  because they can register, add information, and put themselves online so hiring person can see, view them and get connect.', // Add Your Service Type Here
                    //Project Image - Add Your Project Image Here
                    imageSrc: FaLaptop
                },
                {
                    id: 2,
                    title: 'React-Portfolio',
                    service: 'This is a portfolio, which is useful to presents personal information, evidence of relevant skills and abilities.The website provides personal information in depth which includes personal introduction, list of proficient technologies, contact information and list of projects that have been accomplished.',
                    imageSrc: FaLaptop
                },

            ]
        }
    )
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(true);

    const handleExpandClick = (event, id) => {
        console.log("handle expand click")
        const btnName = event.currentTarget.value
        console.log(btnName)
        console.log(id)
        setExpanded(!expanded)

    };


    return (

        <>
            {state.length ? (

                <div>
                    <Grid item xs container spacing={3} >
                        {state.projects.map((project, i) => (


                            <Grid item xs={12} md={12} lg={6}>
                                <Card className={classes.root}>
                                    <CardHeader
                                        avatar={<Avatar aria-label="recipe"
                                            className={classes.avatar}>{project.fields.projectName.charAt(0)}</Avatar>}
                                        action={<IconButton aria-label="settings"></IconButton>}
                                        titleTypographyProps={{ variant: "h4" }}
                                        title={project.fields.projectName}
                                        subheaderTypographyProps={{ variant: "subheader" }}
                                        subheader={project.fields.date}
                                    />

                                    {project.fields.images.map(projectImages => (
                                        <div>
                                            <CardMedia
                                                className={classes.media}
                                                image={projectImages.fields.file.url}
                                                title="Paella dish"
                                            />
                                        </div>
                                    ))}
                                    <CardContent className={classes.cardHeader}>
                                        <Typography variant="h4" color="primary"> Description:</Typography>
                                        <Typography variant="h5" className={classes.description} component="p">
                                            {project.fields.description}
                                        </Typography>
                                    </CardContent>
                                    <CardActions disableSpacing className={classes.cardFooter}>
                                        <IconButton aria-label="live url" onClick={() => window.open((project.fields.liveLink), '_blank')}><LiveTv /></IconButton>
                                        <IconButton aria-label="Github" onClick={() => window.open((project.fields.githubLink), '_blank')}><GitHub /></IconButton>

                                        <IconButton
                                            className={clsx(classes.expand, {
                                                [classes.expandOpen]: expanded,
                                            })}

                                            name={project.sys.id}
                                            value={project.sys.id}
                                            onClick={(event) => handleExpandClick(event, project.sys.id)}
                                            aria-expanded={expanded}
                                            aria-label="show more"
                                        >
                                            {/* <ExpandMoreIcon /> */}
                                        </IconButton>
                                    </CardActions>
                                    <Collapse in={expanded} timeout="auto" unmountOnExit>
                                        <CardContent>
                                            <Typography paragraph variant="h5">Technologies Used:</Typography>
                                            {project.fields.technoligiesUsed.map(technoligiesUsed => (
                                                <div>
                                                    <Typography paragraph variant="h6">
                                                        {technoligiesUsed}
                                                    </Typography>
                                                </div>

                                            ))}

                                        </CardContent>

                                    </Collapse>
                                </Card>
                            </Grid>

                        ))}
                    </Grid>

                </div>
            ) :
                (
                    <h3>No projects Found</h3>

                )}
        </>
    );
}
