import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardHeader, CardMedia, CardContent, CardActions, Collapse, Avatar, IconButton, Typography } from '@material-ui/core';
import { FaLaptop, FaLaptopCode, FaDatabase, FaExpand } from "react-icons/fa"
import { red } from '@material-ui/core/colors';
import { Grid } from '@material-ui/core';
import Fade from 'react-reveal/Fade';
import Flippy, { FrontSide, BackSide } from 'react-flippy';



const useStyles = makeStyles((theme) => ({
    root: {

        margin: 'auto',
        marginBottom: "50px",
        width: "55%",
        height: "auto"

    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9

    },
    expand: {
        transform: 'rotate(0deg)',
        margin: 'auto',

        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',

    },
    avatar: {
        backgroundColor: red[500],
        fontSize: "35px"
    },
    cardHeader: {
        backgroundColor: "#faf0db"
    },
    cardFooter: {
        backgroundColor: "#f3e5ab"
    },
    description: {
        color: "#112222"
    }
}));



export default function Services() {

    //importing useProject context 
    const [state, setState] = useState(
        [
            {
                sys: {
                    id: "1"
                },
                fields: {
                    projectName: "Languages",
                    icon: <FaLaptop />,
                    technoligiesUsed: [
                        "HTML",
                        "CSS",
                        "Javascript",
                        "C/C++"
                    ]
                }
            },

            {
                sys: {
                    id: "2"
                },
                fields: {
                    projectName: "Database",
                    technoligiesUsed: [
                        "MySQL",
                        "MongoDB",
                        "Sqllite",
                        "PostgreSQL"
                    ]
                }
            },
            {
                sys: {
                    id: "3"
                },
                fields: {
                    projectName: "CSS Libraries",
                    technoligiesUsed: [
                        "Bootstrap",
                        "Material UI",
                        "Foundation",
                        "Qt GUI",
                    ]
                }
            },

            {
                sys: {
                    id: "4"
                },
                fields: {
                    projectName: "Front End Libraries",
                    technoligiesUsed: [
                        "React JS",
                        "Next JS",
                        "Apollo",
                        "jQuery",
                        "Handlebars JS",
                        "Qt",
                    ]
                }
            },

            {
                sys: {
                    id: "4"
                },
                fields: {
                    projectName: "Back End Libraries",
                    technoligiesUsed: [
                        "Node JS",
                        "Express JS",
                        "Yoga Server",
                        "Prisma",
                        "Sequilize",
                        "Mongoose"
                    ]
                }
            },
            {
                sys: {
                    id: "6"
                },
                fields: {
                    projectName: "Test/Other",
                    technoligiesUsed: [
                        "Jest",
                        "Bitbucket",
                        "Jira",
                        "Github",
                        "Gitlab",
                        "Heroku"
                    ]
                }
            }
        ]
    )
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(true);

    return (

        <>
            <Flippy
                flipOnHover={true} // default false
                flipOnClick={true} // default false
                flipDirection="horizontal" // horizontal or vertical
                ref={(r) => this.flippy = r} // to use toggle method like this.flippy.toggle()
                // if you pass isFlipped prop component will be controlled component.
                // and other props, which will go to div
                style={{ width: '200px', height: '200px' }} /// these are optional style, it is not necessary
            >


            {state.length ? (
                <FrontSide style={{ backgroundColor: '#41669d' }}>


                <div className="usedTechnologies">
                    <h1 className='proficientTechnologies'>Proficient Technologies.</h1>
                    <Grid item xs container spacing={2} >
                        {state.map((project, i) => (

                            <Grid item xs={12} md={4} >
                                <Card className={classes.root} className="technology">
                                    <CardHeader
                                        avatar={<Avatar aria-label="recipe"
                                            className={classes.avatar}>{project.fields.projectName.charAt(0)}</Avatar>}
                                        action={<IconButton aria-label="settings"></IconButton>}
                                        titleTypographyProps={{ variant: "h4" }}
                                        title={project.fields.projectName}
                                    />



                                    <Collapse in={expanded} timeout="auto" unmountOnExit>
                                        <CardContent>

                                            {project.fields.technoligiesUsed.map(technoligiesUsed => (
                                                <div>
                                                    <Typography paragraph variant="h6">
                                                        {technoligiesUsed}
                                                    </Typography>
                                                </div>

                                            ))}

                                        </CardContent>

                                    </Collapse>
                                </Card>
                            </Grid>

                        ))}

                    </Grid>


                </div>
                </FrontSide>
            ) :
                (

                    <h3>No projects Found</h3>

                )}



            </Flippy>
        </>
    );
}
