import React, { useContext, useEffect, useState } from 'react';
import { CarouselContext } from 'pure-react-carousel';
import { useProjectsContext } from "../Utils/ProjectsContext"

export default function MyComponentUsingContext() {
  const [state, dispatch] = useProjectsContext(carouselContext.state.currentSlide);
 // const carouselContext = useContext(CarouselContext);
  //const [currentSlide, setCurrentSlide] = useState(carouselContext.state.currentSlide);
  useEffect(() => {
    function onChange() {
      setCurrentSlide(carouselContext.state.currentSlide);
    }
    carouselContext.subscribe(onChange);
    return () => carouselContext.unsubscribe(onChange);
  }, [carouselContext]);
  return `The current slide is: ${currentSlide}`;
}
